      if(this.isMounted()) {
        console.log(result);
        this.setState({inv: result.inventory.length});
      }
    }.bind(this));
    var socket = io.connect();
    socket.on('inventory:update', function (data) {
      console.log(data);
      this.setState({inv:data.inventory.length})
    }.bind(this));    

  },
  renderAuthLinks: function() {
    if(this.state.user && Object.keys(this.state.user).length !== 0)
    {
      return (React.createElement("li", null, React.createElement("a", {href: "/auth/logout"}, "Sign out...")));
    }
    return (React.createElement("li", null, React.createElement("a", {href: "/auth/twitter"}, "Sign in...")));

  },
  renderSidebar: function() {
    if(this.state.user && Object.keys(this.state.user).length !== 0)
    {
      return (
        React.createElement("div", {className: "col-xs-6 col-sm-3 sidebar-offcanvas", id: "sidebar"}, 
