            React.createElement(Link, {className: "list-group-item", to: "construction"}, "Construction")
          ), 
          React.createElement("div", {className: "list-group"}, 
            React.createElement(Link, {className: "list-group-item", to: "inventory"}, "Pockets ", React.createElement("span", {className: "badge"}, this.state.inv))
          )
        )
      );
    }
  },
  render: function () {
    var authLinks = this.renderAuthLinks();
    var sidebar = this.renderSidebar();
    return (
        React.createElement("div", null, 
            React.createElement("nav", {className: "navbar navbar-inverse navbar-fixed-top"}, 
              React.createElement("div", {className: "container"}, 
                React.createElement("div", {className: "navbar-header"}, 
