                  React.createElement("p", {className: "pull-right visible-xs"}, 
                    React.createElement("button", {type: "button", className: "btn btn-primary btn-xs", "data-toggle": "offcanvas"}, "Toggle nav")
                  ), 
                  React.createElement(RouteHandler, null)
                ), 
                sidebar
              )
            )
        )
    );
  }
});

module.exports = App;

