    this.setState({fish: null});
  },
  render: function() {
    return (
      React.createElement("div", null, 
        React.createElement("div", {className: "jumbotron"}, 
          React.createElement("h2", null, " Fishing "), 
          React.createElement("p", null, "Here you can fish the village waters.")
        ), 
        React.createElement("div", null, 
          !this.state.fish ? React.createElement("button", {className: "btn btn-primary", onClick: this.handleClick}, "Fish") : React.createElement("p", null), 
          this.state.fish ? React.createElement(FishingResults, {fish: this.state.fish, decision: this.resetAfterFishingDecision}) : React.createElement("p", null)
